import React from "react";
import { useForm } from "react-hook-form";

const AddNewNote = ({ setNotes }) => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    /* Getting the current date and adding it to the data object. */
    let today = new Date();
    const dd = String(today.getDate()).padStart(2, "0");
    const mm = String(today.getMonth() + 1).padStart(2, "0"); //January is 0!
    const yyyy = today.getFullYear();

    today = mm + "/" + dd + "/" + yyyy;
    data.date = today;

    setNotes((prevdata) => [...prevdata, data]);
  };

  return (
    /* A form that is using the react-hook-form library to validate the form. */
    <form
      onSubmit={handleSubmit(onSubmit)}
      style={{ display: "flex", flexDirection: "column" }}
    >
      <h3 style={{ marginLeft: "15px" }}>Title</h3>
      <input
        {...register("title", { required: true })}
        style={{ marginLeft: "15px" }}
      />
      {errors.title && <p>Title is required.</p>}

      <h3 style={{ marginLeft: "15px" }}>Category</h3>
      <input
        {...register("category", { required: true })}
        style={{ marginLeft: "15px" }}
      />
      {errors.category && <p>Category is required.</p>}

      <h3 style={{ marginLeft: "15px" }}>Description</h3>
      <textarea
        {...register("description", { required: true })}
        style={{ marginLeft: "15px" }}
      />
      {errors.description && <p>Please enter description</p>}

      <input type="submit" style={{ marginLeft: "15px" }} />
    </form>
  );
};

export default AddNewNote;
